     },                                                                                                                                                                                                                                                                                                                                                               +
                                                                                                                                                                                                                                                                                                                                                                      +
     docs: 'https://medium.com/@joe.njenga/claude-code-just-cut-mcp-context-bloat-by-46-9-51k-tokens-down-to-8-5k-with-new-tool-search-ddf9e905f734'                                                                                                                                                                                                                  +
   }                                                                                                                                                                                                                                                                                                                                                                  +
 };                                                                                                                                                                                                                                                                                                                                                                   +
