 // ==================================================                                                                                                                                                                                                                                                                                                                +
 // FEATURE SELECTION LOGIC                                                                                                                                                                                                                                                                                                                                           +
 // ==================================================                                                                                                                                                                                                                                                                                                                +
                                                                                                                                                                                                                                                                                                                                                                      +
 // Helper: Select relevant features based on user's setup                                                                                                                                                                                                                                                                                                            +
 // Note: State filtering will be handled by a separate workflow node                                                                                                                                                                                                                                                                                                 +
 // This function just determines which features are relevant based on project configuration                                                                                                                                                                                                                                                                          +
 function selectRelevantFeatures(project, recStates) {                                                                                                                                                                                                                                                                                                                           +
   const features = [];                                                                                                                                                                                                                                                                                                                                               +
                                                                                                                                                                                                                                                                                                                                                                      +
   // Always recommend these (universal benefits)                                                                                                                                                                                                                                                                                                                     +
   features.push(claudeCodeFeatures.sessionStartHooks);                                                                                                                                                                                                                                                                                                               +
   features.push(claudeCodeFeatures.denyRules);                                                                                                                                                                                                                                                                                                                       +
   features.push(claudeCodeFeatures.toolSearch);                                                                                                                                                                                                                                                                                                                      +
                                                                                                                                                                                                                                                                                                                                                                      +
   // If user has commands, recommend disable-model-invocation                                                                                                                                                                                                                                                                                                        +
   if (project.commands && project.commands.includes('done')) {                                                                                                                                                                                                                                                                                                       +
     features.push(claudeCodeFeatures.disableModelInvocation);                                                                                                                                                                                                                                                                                                        +
   }                                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   // If user has multiple projects, recommend GitHub MCP                                                                                                                                                                                                                                                                                                             +
   if (project.project_name === 'Dev') {                                                                                                                                                                                                                                                                                                                              +
     features.push(claudeCodeFeatures.githubMCP);                                                                                                                                                                                                                                                                                                                     +
   }                                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   return features;                                                                                                                                                                                                                                                                                                                                                   +
 }                                                                                                                                                                                                                                                                                                                                                                    +
