                                                                                                                                                                                                                                                                                                                                                                      +
 // Generate action URLs for recommendation state management                                                                                                                                                                                                                                                                                                          +
 // Simplified approach: use rule_id + project_id directly (no pre-generated tokens)                                                                                                                                                                                                                                                                                  +
 // Tokens will be generated on-the-fly by webhook handler for security                                                                                                                                                                                                                                                                                               +
 function generateActionURLs(ruleId, projectId) {                                                                                                                                                                                                                                                                                                                     +
   const baseUrl = 'http://192.168.1.11:5678/webhook/recommendation-action';                                                                                                                                                                                                                                                                                          +
   const actions = ['dismiss', 'remind', 'complete'];                                                                                                                                                                                                                                                                                                                 +
   const urls = {};                                                                                                                                                                                                                                                                                                                                                   +
                                                                                                                                                                                                                                                                                                                                                                      +
   for (const action of actions) {                                                                                                                                                                                                                                                                                                                                    +
     // Generate a simple token from rule_id + project_id + timestamp                                                                                                                                                                                                                                                                                                 +
     // The webhook handler will validate and create a proper audit entry                                                                                                                                                                                                                                                                                             +
     const timestamp = Date.now();                                                                                                                                                                                                                                                                                                                                    +
     const simpleToken = Buffer.from(`${ruleId}:${projectId}:${action}:${timestamp}`).toString('base64');                                                                                                                                                                                                                                                             +
                                                                                                                                                                                                                                                                                                                                                                      +
     urls[action] = `${baseUrl}?rule_id=${encodeURIComponent(ruleId)}&project_id=${encodeURIComponent(projectId)}&action=${action}&token=${simpleToken}`;                                                                                                                                                                                                             +
   }                                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   return urls;                                                                                                                                                                                                                                                                                                                                                       +
 }                                                                                                                                                                                                                                                                                                                                                                    +
                                                                                                                                                                                                                                                                                                                                                                      +
