 </head>                                                                                                                                                                                                                                                                                                                                                              +
 <body>                                                                                                                                                                                                                                                                                                                                                               +
   <h1>📊 Claude Config Report</h1>                                                                                                                                                                                                                                                                                                                                   +
   <p style="color: #666;">Generated: ${date}</p>                                                                                                                                                                                                                                                                                                                     +
   <p style="color: #666;">Health Score: <strong>${healthScore}%</strong></p>                                                                                                                                                                                                                                                                                         +
                                                                                                                                                                                                                                                                                                                                                                      +
   <div class="summary">                                                                                                                                                                                                                                                                                                                                              +
     <h2>Summary</h2>                                                                                                                                                                                                                                                                                                                                                 +
     <ul>                                                                                                                                                                                                                                                                                                                                                             +
       <li><span class="num">${totalProjects}</span> ${totalProjects === 1 ? 'Repository' : 'Repositories'}</li>                                                                                                                                                                                                                                                      +
       <li><span class="num">${totalFiles}</span> Claude Files</li>                                                                                                                                                                                                                                                                                                   +
       <li><span class="num">${totalSkills}</span> Skills</li>                                                                                                                                                                                                                                                                                                        +
       <li><span class="num">${totalCommands}</span> Commands</li>                                                                                                                                                                                                                                                                                                    +
     </ul>                                                                                                                                                                                                                                                                                                                                                            +
   </div>                                                                                                                                                                                                                                                                                                                                                             +
 `;                                                                                                                                                                                                                                                                                                                                                                   +
                                                                                                                                                                                                                                                                                                                                                                      +
 // Build project sections with recommendations                                                                                                                                                                                                                                                                                                                       +
 for (const project of projects) {                                                                                                                                                                                                                                                                                                                                    +
   html += `                                                                                                                                                                                                                                                                                                                                                          +
   <div class="repo">                                                                                                                                                                                                                                                                                                                                                 +
     <h2>${project.project_name}</h2>                                                                                                                                                                                                                                                                                                                                 +
     <p style="color: #666; margin: 5px 0;">Path: <code>${project.project_path}</code></p>                                                                                                                                                                                                                                                                            +
     <p style="color: #666; margin: 5px 0;">Files: ${project.file_count || 0}</p>                                                                                                                                                                                                                                                                                     +
 `;                                                                                                                                                                                                                                                                                                                                                                   +
                                                                                                                                                                                                                                                                                                                                                                      +
   // Skills                                                                                                                                                                                                                                                                                                                                                          +
   if (project.skills && project.skills.length > 0) {                                                                                                                                                                                                                                                                                                                 +
     html += `<h3>Skills (${project.skills.length})</h3><div>`;                                                                                                                                                                                                                                                                                                       +
     for (const skill of project.skills) {                                                                                                                                                                                                                                                                                                                            +
       html += `<span class="badge badge-skill">${skill}</span>`;                                                                                                                                                                                                                                                                                                     +
     }                                                                                                                                                                                                                                                                                                                                                                +
     html += `</div>`;                                                                                                                                                                                                                                                                                                                                                +
   }                                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   // Commands                                                                                                                                                                                                                                                                                                                                                        +
   if (project.commands && project.commands.length > 0) {                                                                                                                                                                                                                                                                                                             +
     html += `<h3>Commands (${project.commands.length})</h3><div>`;                                                                                                                                                                                                                                                                                                   +
     for (const cmd of project.commands) {                                                                                                                                                                                                                                                                                                                            +
       html += `<span class="badge badge-command">/${cmd}</span>`;                                                                                                                                                                                                                                                                                                    +
     }                                                                                                                                                                                                                                                                                                                                                                +
     html += `</div>`;                                                                                                                                                                                                                                                                                                                                                +
   }                                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   html += `</div>`;                                                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   // Generate personalized recommendations for this project                                                                                                                                                                                                                                                                                                          +
   const features = selectRelevantFeatures(project, recStates);                                                                                                                                                                                                                                                                                                                  +
                                                                                                                                                                                                                                                                                                                                                                      +
   if (features.length > 0) {                                                                                                                                                                                                                                                                                                                                         +
     html += `<div style="margin-top: 30px;">`;                                                                                                                                                                                                                                                                                                                       +
     html += `<h2>💡 Personalized Claude Code Enhancements for ${project.project_name}</h2>`;                                                                                                                                                                                                                                                                         +
     html += `<p style="color: #666; margin-bottom: 20px;">These features will improve your workflow and productivity. Each recommendation includes: what it is, why you need it, how it works in practice, and exact implementation steps.</p>`;                                                                                                                     +
                                                                                                                                                                                                                                                                                                                                                                      +
     html += generateEducationalRecommendations(features, project.project_name);                                                                                                                                                                                                                                                                                      +
                                                                                                                                                                                                                                                                                                                                                                      +
     html += `</div>`;                                                                                                                                                                                                                                                                                                                                                +
   }                                                                                                                                                                                                                                                                                                                                                                  +
 }                                                                                                                                                                                                                                                                                                                                                                    +
                                                                                                                                                                                                                                                                                                                                                                      +
 html += `                                                                                                                                                                                                                                                                                                                                                            +
   <p style="margin-top: 30px; color: #666; font-size: 12px; text-align: center;">                                                                                                                                                                                                                                                                                    +
     🤖 Generated by Claude Config Auditor                                                                                                                                                                                                                                                                                                                            +
   </p>                                                                                                                                                                                                                                                                                                                                                               +
 </body>                                                                                                                                                                                                                                                                                                                                                              +
 </html>`;                                                                                                                                                                                                                                                                                                                                                            +
                                                                                                                                                                                                                                                                                                                                                                      +
 // Return the result                                                                                                                                                                                                                                                                                                                                                 +
 return [{                                                                                                                                                                                                                                                                                                                                                            +
   json: {                                                                                                                                                                                                                                                                                                                                                            +
     html,                                                                                                                                                                                                                                                                                                                                                            +
     totalFiles: totalFiles,                                                                                                                                                                                                                                                                                                                                          +
     totalRepos: totalProjects,                                                                                                                                                                                                                                                                                                                                       +
     totalSkills: totalSkills,                                                                                                                                                                                                                                                                                                                                        +
     totalCommands: totalCommands                                                                                                                                                                                                                                                                                                                                     +
   }                                                                                                                                                                                                                                                                                                                                                                  +
 }];                                                                                                                                                                                                                                                                                                                                                                  +
